


INSERT INTO movies(release_year, title, synopsis,price,engagement_id,director_id) VALUES (1994,'The Shawshank Redemption',
                                                               'Andy Dufresne (Tim Robbins) is sentenced to two consecutive ' ||
                                                               'life terms in prison for the murders of his wife and her lover ' ||
                                                               'and is sentenced to a tough prison.However, only Andy knows he' ||
                                                               ' didn''t commit the crimes. While there, he forms a friendship with ' ||
                                                               'Red (Morgan Freeman), experiences brutality of prison life, adapts, ' ||
                                                               'helps the warden, etc., all in 19 years.',5.99,1,1);

INSERT INTO movies( release_year, title, synopsis,price,engagement_id,director_id) VALUES (2013,'Thor: The Dark World',
                                                              'In ancient times, the gods of Asgard fought and won a war against ' ||
                                                              'an evil race known as the Dark Elves. The survivors were neutralized,' ||
                                                              ' and their ultimate weapon -- the Aether -- was buried in a secret ' ||
                                                              'location. Hundreds of years later, Jane Foster (Natalie Portman) finds ' ||
                                                              'the Aether and becomes its host, forcing Thor (Chris Hemsworth) to bring ' ||
                                                              'her to Asgard before Dark Elf Malekith (Christopher Eccleston) captures ' ||
                                                              'her and uses the weapon to destroy the Nine Realms -- including Earth.',20.99,3,2);
