INSERT INTO movies(release_year, title, synopsis,price,engagement_id,director_id) VALUES (2021,'Shang-Chi and The Legend of The Ten Rings',
                                                              'Martial-arts master Shang-Chi confronts the past he thought he left behind when ' ||
